mapper.update:
update tr_contents
set content = #{content},
    status = #{status},
    update_date = now()
where id = #{id}
and member_id = #{memberid}

update tr_contents
set content2 = #{content},
    status = #{status},
    update_date = now()
where id = #{id}
and member_id = #{memberid}

update tr_contents
set content3 = #{content},
    status = #{status},
    update_date = now()
where id = #{id}
and member_id = #{memberid}

update tr_contents
set status = #{status},
    update_date = now()
where id = #{id}
and member_id = #{memberid}

mapper.select:
select a.id, a.member_id as memberid, a.title, a.keyword, a.category_sub1 as categorysub1, a.category_sub2 as categorysub2, a.thumb_image as thumbimage, a.create_date as createdate, a.update_date as updatedate, a.toring, a.status, (select nickname from tr_profile where member_id = a.member_id) as nickname from tr_contents a
where a.title like #{keyword1}
or a.title like #{keyword2}
or a.title like #{keyword3}
or a.title like #{keyword4}
or a.title like #{keyword5}
order by createdate desc
